{
  name: 'Interactive: Dot Notification',
  render: () => ({
    template: `
      <div style="padding: 24px;">
        <p style="margin-bottom: 16px; color: #666;">
          Verifying dot mode accessibility and structure
        </p>
        <div style="position: relative; display: inline-block; padding: 12px 24px; background: #f5f5f5; border-radius: 8px;">
          <span data-testid="notification-text">Notifications</span>
          <zg-badge 
            variant="error" 
            [dot]="true" 
            data-testid="dot-badge"
            style="position: absolute; top: 8px; right: 8px;"
          ></zg-badge>
        </div>
      </div>
    `,
  }),
  play: async ({ canvasElement }) => {
    const canvas = within(canvasElement);

    // Find the dot badge
    const dotBadge = canvas.getByTestId('dot-badge');
    await expect(dotBadge).toBeInTheDocument();

    // Verify badge has role="status"
    await expect(dotBadge).toHaveAttribute('role', 'status');

    // Verify badge has dot class
    await expect(dotBadge).toHaveClass('zg-badge--dot');

    // Find the dot element inside
    const dotElement = canvasElement.querySelector('.zg-badge__dot');
    await expect(dotElement).toBeInTheDocument();

    // Verify dot has aria-label
    await expect(dotElement).toHaveAttribute('aria-label', 'notification indicator');

    // Verify no text content is shown
    const content = canvasElement.querySelector('.zg-badge__content');
    await expect(content).not.toBeInTheDocument();
  },
}

