{
  name: 'Interactive: Keyboard Navigation',
  render: () => ({
    template: `
      <div style="padding: 24px;">
        <p style="margin-bottom: 16px; color: #666;">
          Watch the interaction: Tab key will navigate through badges, Enter will activate
        </p>
        <div style="display: flex; gap: 12px; flex-wrap: wrap;">
          <zg-badge 
            variant="primary" 
            text="First Badge" 
            [removable]="true"
            data-testid="badge-1"
          ></zg-badge>
          <zg-badge 
            variant="success" 
            text="Second Badge" 
            [removable]="true"
            data-testid="badge-2"
          ></zg-badge>
          <zg-badge 
            variant="info" 
            text="Third Badge" 
            [removable]="true"
            data-testid="badge-3"
          ></zg-badge>
        </div>
      </div>
    `,
  }),
  play: async ({ canvasElement }) => {
    const canvas = within(canvasElement);

    // Find all close buttons
    const closeButtons = canvas.getAllByRole('button', { name: /remove badge/i });
    await expect(closeButtons).toHaveLength(3);

    // Tab to first button
    await userEvent.tab();
    await expect(closeButtons[0]).toHaveFocus();

    // Wait a moment to show focus
    await new Promise((resolve) => setTimeout(resolve, 500));

    // Tab to second button
    await userEvent.tab();
    await expect(closeButtons[1]).toHaveFocus();

    // Wait a moment
    await new Promise((resolve) => setTimeout(resolve, 500));

    // Tab to third button
    await userEvent.tab();
    await expect(closeButtons[2]).toHaveFocus();

    // Wait before pressing Enter
    await new Promise((resolve) => setTimeout(resolve, 500));

    // Press Enter to activate
    await userEvent.keyboard('{Enter}');
  },
}

{
  name: 'Interactive: Numeric Max Verification',
  render: () => ({
    template: `
      <div style="padding: 24px;">
        <p style="margin-bottom: 16px; color: #666;">
          Verifying max number logic works correctly
        </p>
        <div style="display: flex; gap: 24px; align-items: center; flex-wrap: wrap;">
          <div style="display: flex; flex-direction: column; align-items: center; gap: 8px;">
            <zg-badge 
              variant="error" 
              text="50" 
              [max]="99"
              data-testid="badge-below-max"
            ></zg-badge>
            <span style="font-size: 12px; color: #666;">Below max (50)</span>
          </div>
          
          <div style="display: flex; flex-direction: column; align-items: center; gap: 8px;">
            <zg-badge 
              variant="error" 
              text="99" 
              [max]="99"
              data-testid="badge-at-max"
            ></zg-badge>
            <span style="font-size: 12px; color: #666;">At max (99)</span>
          </div>
          
          <div style="display: flex; flex-direction: column; align-items: center; gap: 8px;">
            <zg-badge 
              variant="error" 
              text="150" 
              [max]="99"
              data-testid="badge-above-max"
            ></zg-badge>
            <span style="font-size: 12px; color: #666;">Above max (99+)</span>
          </div>
        </div>
      </div>
    `,
  }),
  play: async ({ canvasElement }) => {
    const canvas = within(canvasElement);

    // Badge below max should show actual number
    const badgeBelowMax = canvas.getByTestId('badge-below-max');
    await expect(badgeBelowMax).toHaveTextContent('50');

    // Badge at max should show exact max
    const badgeAtMax = canvas.getByTestId('badge-at-max');
    await expect(badgeAtMax).toHaveTextContent('99');

    // Badge above max should show "max+"
    const badgeAboveMax = canvas.getByTestId('badge-above-max');
    await expect(badgeAboveMax).toHaveTextContent('99+');

    // Verify all have correct variant
    await expect(badgeBelowMax).toHaveAttribute('data-variant', 'error');
    await expect(badgeAtMax).toHaveAttribute('data-variant', 'error');
    await expect(badgeAboveMax).toHaveAttribute('data-variant', 'error');
  },
}

